#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n,q;    cin>>n;    vector<ll>arr(n);    f(n,arr);    sort(begin(arr),end(arr));    vector<ll>res;    ll sum=accumulate(begin(arr),end(arr),0ll);    cin>>q;    for (ll i = 0; i < q; i++)    {        ll a,b;        cin>>a>>b;        // largest number <=a;        auto it=lower_bound(begin(arr),end(arr),a);        // smallest element greater than a        ll ans=LLONG_MAX;        if(it!=arr.end()){            ll h=*it;            ll cost=max(0ll,a-h) + max(0ll,b-(sum-h));            ans=min(ans,cost);        }        // largest element smaller than a        if(it!=arr.begin()){            ll h=*(it-1);            ll cost=max(0ll,a-h)+max(0ll,b-(sum-h));            ans=min(ans,cost);        }        res.push_back(ans);    }    for(ll i:res)cout<<i<<'\n';} int main(){solve();     return 0;}