#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n,k;    cin>>n>>k;    vector<ll>arr(n);    f(n,arr);    sort(begin(arr),end(arr));    // right movement from last right to 0    ll i=n-1;    ll dist=0;    while (i>=0 && arr[i]>=0)    {        dist+=(2*arr[i]);        i-=k;     }    i=0;    while (i<n && arr[i]<0)    {        dist+=abs(2*arr[i]);        i+=k;    }    dist-=(max(abs(arr[0]),arr[n-1]));    cout<<dist<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}