#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<ll>arr(n);    f(n,arr);    string s;    cin>>s;    priority_queue<ll>pq_r;    priority_queue<ll,vector<ll>,greater<ll>>pq_l;    for (ll i = 0; i < n; i++)    {        if(s[i]=='R')pq_r.push(arr[i]);        else pq_l.push(arr[i]);    }    ll j=n;    while (!pq_r.empty())    {        if(pq_r.top()<=j){            pq_r.pop();        }        else {            cout<<"NO"<<'\n';            return;        }        j--;    }    j=1;    while (!pq_l.empty())    {        if(pq_l.top()>=j){            pq_l.pop();        }        else{            cout<<"NO"<<'\n';            return;        }        j++;    }    cout<<"YES"<<'\n';    } int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}