#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}ll total(ll n,ll mid){    if(mid<=n)return (ll)mid*(mid+1)/2;    ll rem=2*n-1-mid;    return (ll)n*n-rem*(rem+1)/2;}void solve(){    ll n,x;    cin>>n>>x;        ll l=1,r=2*n-1,ans=2*n-1;        while (l<=r)    {        ll mid=l+(r-l)/2;        if(total(n,mid)>=x){            ans=mid;            r=mid-1;        }        else l=mid+1;    }    cout<<ans<<'\n';    } int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}