#include<bits/stdc++.h>using namespace std;typedef long long ll;const ll N=2*1e5;vector<vector<ll>>bit_counts(30,vector<ll>(N+1,0));void solve(){    ll l,r;    cin>>l>>r;    ll n=r-l+1;    ll mx=0;    for (ll i = 0; i < 30; i++)        mx=max(mx,bit_counts[i][r]-bit_counts[i][l-1]);    cout<<n-mx<<'\n';} int main(){ for (ll i = 1; i <=N; i++)    for (ll b = 0; b < 30; b++)       bit_counts[b][i]=bit_counts[b][i-1]+((i>>b)&1);     ll t;cin>>t;while (t--){    solve();}    return 0;}