#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}// ṣtarting from end find number of shifts required to shift to its correct position and also track relative positionsvoid solve(){    ll n;    cin>>n;    vector<ll>arr(n),pos(n+1);    for (ll i = 0; i < n; i++)    {       cin>>arr[i];       pos[arr[i]]=i+1;    }    vector<ll>ans(n);    for (ll i = n; i>=1; i--)    {        ll req=pos[i]%i;        ans[i-1]=req;        pos[i]=i;        for (ll j = 1; j <n; j++)            pos[j]=(pos[j]-req+i)%i;            }    for(ll i:ans)cout<<i<<" ";    cout<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}