#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<string>&a){    for(ll i=0;i<n;i++)cin>>a[i];} void solve(){    ll n;    cin>>n;    unordered_map<string,ll>mp;    for (ll i = 0; i < n; i++)    {        string s;        cin>>s;        mp[s]++;    }    ll cnt=0;    for(auto it=mp.begin();it!=mp.end();it++){        string k = it->first;        ll temp=it->second;        for (ll i = 0; i <=1; i++)        {                    char keep=k[i];            for(char c='a'; c<='k'; c++)            {               if(c==keep)continue;                k[i]=c;               if(mp.find(k)!=mp.end())cnt+=mp[k]*temp;            }            k[i]=keep;        }    }    cout<<cnt/2<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}