#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n,k;    cin>>n>>k;    vector<ll>arr(n);    f(n,arr);    vector<ll>bit_lists(31,0);    for (ll i = 0; i<31; i++)    {        for (ll j = 0; j < n; j++)            if((arr[j]>>i)&1)bit_lists[i]++;    }    for (ll i = 30; i>=0; i--)    {        ll cnt=n-bit_lists[i];        if( cnt<=k ){            bit_lists[i]=n;            k-=cnt;         }    }    ll ans=0;    for (ll i = 0; i < 31; i++)    {        if(bit_lists[i]==n)ans+=(1<<i);    }    cout<<ans<<'\n';    }Â int main(){ll t;cin>>t;while (t--){    solve();}    return 0;}