#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<ll>par(n+1);    vector<bool>leaf(n+1,true),vis(n+1,false);    par[1]=1;    for (ll i=2;i<=n;i++){        cin>>par[i];        leaf[par[i]]=false;    }      string s;    cin>>s;    vector<pair<ll,ll>>vec(n+1);    for (ll i = 1; i<=n; i++)    {       if(leaf[i]){        ll w=0,b=0;        ll j=i;        while (par[j]!=j)        {            if(!vis[j]){            if(s[j-1]=='W')w++;            else b++;            vis[j]=true;            }            vec[j].first+=w,vec[j].second+=b;            j=par[j];        }        if(j==par[j]){            if(!vis[j]){            if(s[j-1]=='W')w++;            else b++;            vis[j]=true;            }            vec[j].first+=w,vec[j].second+=b;        }       }    }    ll cnt=0;    for (ll i = 1; i <=n; i++)    {        if(vec[i].first==vec[i].second)cnt++;    }    cout<<cnt<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}