#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<ll>arr(n);    f(n,arr);    ll curr_sum=0;    bool ones_zero=0;    for (ll i = 0; i < n; i++)    {        if(arr[i]!=0 && ones_zero){            cout<<"No"<<'\n';            return;        }        curr_sum+=arr[i];        if(curr_sum<0){            cout<<"No"<<'\n';            return;        }        else if (curr_sum==0)ones_zero=1;      }   if(ones_zero)cout<<"Yes"<<'\n';   else cout<<"No"<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}