#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    // if >=3 positive numbers then NO    vector<ll>pos,neg,zero;    for (ll i = 0; i < n; i++)    {       ll x;       cin>>x;       if(x>0)pos.push_back(x);       else if (x<0)neg.push_back(x);       else{        if(zero.size()<2)zero.push_back(x);       }    }    if((pos.size()>2 || neg.size()>2)){        cout<<"NO"<<'\n';        return;    }    for(ll i:pos)zero.push_back(i);    for(ll i:neg)zero.push_back(i);     for (ll i = 0; i < zero.size(); i++)    {       for (ll j = i+1; j <zero.size(); j++)       {        for (ll k = j+1; k < zero.size(); k++)        {            bool f=0;            for (ll l = 0; l < zero.size(); l++)            {                if(zero[i]+zero[j]+zero[k]==zero[l])f=1;            }             if(!f){                cout<<"NO"<<'\n';                return;            }        }       }    }        cout<<"YES"<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}