#include<bits/stdc++.h>using namespace std;bool Primes[1001];void precompute(){    for (int i = 0; i<=1000; i++)Primes[i]=true;    Primes[0]=Primes[1]=false;    for (int i = 2;i*i<=1000; i++)    {        if(Primes[i]){            for (int j=i*i; j<=1000; j+=i)                Primes[j]=false;                    }    }}void solve(){    int n,k;    cin>>n>>k;        int i=5,j=7;    while (i+j+1<=n && k>0)    {        if(Primes[i+j+1]){            // cout<<i+j+1<<" "<<k<<"\n";            k--;        }        i=j;        j++;        while (!Primes[j])j++;    }    cout<<((k==0)?"YES":"NO")<<'\n';} int main(){ precompute();solve();     return 0;}