#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<ll>arr(n);    f(n,arr);    vector<pair<ll,ll>>vec;    vector<bool>vis(n+1,false);    for (ll i = 0; i < n; i++)        if(arr[i]<i+1){            vec.push_back({i+1,arr[i]});            vis[i+1]=true;        }        vector<ll>ranges(n+1,0);    for(auto p:vec){        ll k=p.second;        if(k>1){            ranges[1]++,ranges[k]--;        }    }    ll cnt=0;    for (ll i = 1; i<=n; i++)    {        ranges[i]+=ranges[i-1];        if(vis[i])cnt+=ranges[i];    }    cout<<cnt<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}