#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<ll>a(n),b(n);    f(n,a),f(n,b);    for (ll i = 0; i < n; i++)    {        if(a[i]>b[i] || (a[i]<b[i] &&  b[i]-1>b[(i+1)%n])){            cout<<"NO"<<'\n';            return;        }    }    cout<<"YES"<<'\n';    } int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}