#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<ll>a(n);    f(n,a);    if(n==1){        cout<<0<<'\n';        return;    }    vector<pair<ll,ll>>vec;    if(a[0]!=a[n-1]){    if((a[0]+a[n-1])&1)a[n-1]=a[0];    else a[0]=a[n-1];    vec.push_back({1,n});    }    for (ll i = 1; i < n-1; i++)    {        if(a[i]!=a[0]){        if((a[i]+a[0])&1)            vec.push_back({1,i+1});        else vec.push_back({i+1,n});        }    }    if(vec.size()==0)cout<<0<<'\n';    else{    cout<<vec.size()<<'\n';    for(auto it:vec)cout<<it.first<<" "<<it.second<<'\n';    }} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}