#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<ll>arr(n);    f(n,arr);    ll ans=1;    ll prev=1;    for (ll i = 1; i < n; i++)    {        ll high=min(arr[i],i+1);        ll low=1;        ll temp_ans=0;        while (high>=low)        {            ll mid=low+(high-low)/2;            if(prev>=mid-1)temp_ans=mid,low=mid+1;            else high=mid-1;        }        prev=temp_ans;        ans+=temp_ans;    }    cout<<ans<<'\n';} int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}     return 0;}