#include<bits/stdc++.h>using namespace std;typedef long long ll;ll bs_sqrt(ll x) {  ll left = 0, right = 2000000123;  while (right > left) {      ll mid = (left + right) / 2;            if (mid * mid > x) right = mid;      else left = mid + 1;  }  return left - 1;}void solve(){    ll l,r;    cin>>l>>r;    ll sql=bs_sqrt(l),sqr=bs_sqrt(r);    ll ans=0;     if(sql==sqr){        for (ll i = 0; i < 3; i++)        {            ll val=sql*(sql+i);            if(val>=l && val<=r)ans++;        }    }    else{        ans=3*(sqr-sql-1);        for (ll i = 0; i < 3; i++)        {            ll val1=sql*(sql+i);            ll val2=sqr*(sqr+i);            if(l<=val1 && r>=val1)ans++;            if(l<=val2 && r>=val2)ans++;        }    }    cout<<ans<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}