#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}ll count(ll n){    ll cnt=0;    while (n%2==0)    {        n/=2;        cnt++;    }    return cnt;}void solve(){    ll n;    cin>>n;    vector<ll>arr(n);    f(n,arr);     ll cnt=0;    for (ll i = 0; i < n; i++)    {        cnt+=(count(arr[i]));    }    if(cnt>=n){        cout<<0<<'\n';        return;    }     ll req = n-cnt;     vector<ll>av;    for (ll i = 1; i <=n; i++)    {        av.push_back(count(i));    }    sort(rbegin(av),rend(av));     ll ops=0;    for(ll c:av){        if(req<=0)break;        if(c>0){            req-=c;            ops++;        }    }     if(req>0)cout<<-1<<'\n';    else cout<<ops<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}