#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<ll>arr(n);    f(n,arr);    ll sum=0;    for(ll i:arr)sum+=i;    sum=abs(sum);    if(sum%2==1){        cout<<-1<<'\n';        return;    }    vector<pair<ll,ll>>vec;    for (ll i = 0; i<n-1; i+=2)    {        if(arr[i]==arr[i+1]){            vec.push_back({i+1,i+2});        }        else{            vec.push_back({i+1,i+1});            vec.push_back({i+2,i+2});        }    }    cout<<vec.size()<<'\n';    for(auto it:vec)cout<<it.first<<" "<<it.second<<'\n'; } int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}