#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<ll>arr(n);    f(n,arr);    vector<ll>ans;    for (ll i=0; i<n; i++)    {        // ll val=arr[i];        // ll j=i;        // ll k=1;        // while (j>=0 && arr[j]/k>=1)        // {        //    j--;        //    k++;        // }        // ans.push_back(i-j);         ll left=0,right=i;        while (left<=right)        {            ll mid=left+(right-left)/2;            if(arr[mid]/(i-mid+1)>=1)right=mid-1;            else left=mid+1;        }        ans.push_back(i-right);            }    for(ll i :ans)cout<<i<<" ";    cout<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}