#include <bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve() {    ll n,d,a;    cin>>n>>d>>a;    vector<ll>arr(n);    f(n,arr);    sort(begin(arr),end(arr));    ll cost=0,mincost=LLONG_MAX;    if(arr[0]!=1){        cost+=a;        mincost=cost+n*d;    }    ll prev=1;    for (ll i=0; i < n; i++)    {        if(i==0 && arr[i]==1){            mincost=(n-1)*d;            continue;        }        while(i<n && arr[i]==prev){            cost+=d;            i++;        }        if(i==n){            cout<<mincost<<'\n';            return;        }        cost+=(arr[i]-prev-1)*a;        // to make permutation of arr[i];        ll temp_cost=cost+(n-i-1)*d;        mincost=min(mincost,temp_cost);        prev=arr[i];    }    cout<<mincost<<'\n';}Â int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);    int t;    cin >> t;    while (t--) solve();}