#include <bits/stdc++.h>using namespace std;typedef long long ll; void solve() {    ll n, c, d;    cin >> n >> c >> d;    vector<ll> a(n);    for (auto &x : a) cin >> x;     sort(a.begin(), a.end());    a.erase(unique(a.begin(), a.end()), a.end());    ll m = a.size();     ll base = (n - m) * c;    ll ans = n * c + d; // remove all + insert 1     for (int i = 0; i < m; i++) {        ll rem = (m - i - 1) * c;        ll ins = (a[i] - (i + 1)) * d;        ans = min(ans, base + rem + max(0LL, ins));    }     cout << ans << "\n";} int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);    int t;    cin >> t;    while (t--) solve();}