#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<ll>arr(n);    f(n,arr);    if(n&1){        cout<<"YES"<<'\n';        return;    }    for (ll i = 1; i < n-1; i++)    {        ll diff=arr[i-1]-arr[i];        arr[i]+=diff;        arr[i+1]+=diff;    }    if(arr[n-1]<arr[n-2])cout<<"NO"<<'\n';    else cout<<"YES"<<'\n';    } int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}