#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    string s;    cin>>s;    bool regular=1;    if(s[0]==')')regular=0;    vector<ll>store_idx;    // jump between regular and irregular    ll open=0,close=0;        for (ll i = 0; i < n; i++)    {        s[i]=='('?open++:close++;        if(regular){            if(close>open){                store_idx.push_back(i-1);                regular=!regular;                close=1,open=0;            }        }        else{            if(open>close){                store_idx.push_back(i-1);                regular=!regular;                open=1,close=0;            }        }    }    if(open!=close){        cout<<-1<<'\n';        return;    }    store_idx.push_back(n-1);    if(store_idx.size()==1)cout<<1<<'\n';    else cout<<2<<'\n';    ll cnt=1,j=0;    for (ll i = 0; i < n; i++)    {        cout<<cnt<<" ";        if(i==store_idx[j]){            cnt=3-cnt;            j++;        }    }    cout<<'\n';} int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}     return 0;}