#include<bits/stdc++.h>using namespace std;typedef long long ll;ll dfs(ll node,ll parent,vector<ll>&cnt,vector<vector<ll>>&adj){        bool isleaf=true;    ll sum=0;     for(auto it:adj[node]){        if(it==parent)continue;        isleaf=false;        sum+=dfs(it,node,cnt,adj);    }    if(isleaf)sum=1;    return cnt[node]=sum;}void solve(){    ll n,q;    cin>>n;    vector<vector<ll>>adj(n);    for (ll i = 0; i <n-1; i++)    {      ll u,v;      cin>>u>>v;      adj[u-1].push_back(v-1);      adj[v-1].push_back(u-1);    }    vector<ll>cnt(n,0);    dfs(0,-1,cnt,adj);    cin>>q;    for (ll i = 0; i < q; i++)    {        ll x,y;        cin>>x>>y;        x--,y--;        cout<<cnt[x]*cnt[y]<<'\n';    }                    } int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}