#include<bits/stdc++.h>using namespace std;typedef long long ll;set<ll>st;void precomp(){    for (ll k = 2; k <=1000; k++)    {        ll val=1+k;        ll p=k*k;        for (ll j = 2; j <=20; j++)        {            val+=p;            if(val>1e6)break;            st.insert(val);            p*=k;        }            }}void solve(){    ll n;    cin>>n;    if(st.count(n))cout<<"YES"<<'\n';    else cout<<"NO"<<'\n';   } int main(){ll t;cin>>t;precomp();while (t--){    solve();}     return 0;}