#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<ll>arr(n);    f(n,arr);    vector<ll>freq(n+1,0);    for(ll x:arr)if(x<=n)freq[x]++;     vector<ll>cnt(n+1,0);    for (ll i = 1; i <=n; i++)    {        if(freq[i]==0)continue;        for (ll j = i; j <=n; j+=i)            cnt[j]+=freq[i];    }    cout<<*max_element(begin(cnt),end(cnt))<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}