#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<ll>a(n);    f(n,a);    vector<pair<ll,ll>>c(n);    for (ll i = 0; i < n; i++)    {        ll temp;        cin>>temp;        c[i].first=a[i]-temp;        c[i].second=i;    }    sort(rbegin(c),rend(c));    ll val=c[0].first;    vector<ll>res;    ll i=0;    while (i<n && c[i].first==val)    {        res.push_back(c[i].second);        i++;    }    sort(begin(res),end(res));    cout<<res.size()<<'\n';    for(ll i:res)cout<<i+1<<" ";    cout<<'\n';} int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}     return 0;}