#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void factorize(ll val,unordered_map<ll,ll>&mp){     for (ll i = 2; i*i<=val; i++)    {       while (val%i==0)       {          val/=i;          mp[i]++;       }    }    if(val>1)mp[val]++;    return;    } void solve(){    ll n;    cin>>n;    vector<ll>arr(n);    unordered_map<ll,ll>mp;    for (ll i = 0; i < n; i++)    {        cin>>arr[i];        factorize(arr[i],mp);    }    for(auto p:mp){        if((p.second%n)!=0){            cout<<"NO"<<'\n';            return;        }    }    cout<<"YES"<<'\n';    return;    } int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}     return 0;}