#include<bits/stdc++.h>using namespace std;void f(int n,vector<int>&a){    for(int i=0;i<n;i++)cin>>a[i];}void solve(){    int n;    cin>>n;    vector<vector<bool>>vis(n,vector<bool>(51,false));    vector<bool>present(51,false);    for (int i = 0; i < n; i++)    {        int a;        cin>>a;        for (int j = 0; j < a; j++)        {            int b;            cin>>b;            vis[i][b]=true;            present[b]=true;        }    }    int mx_cnt=0;     for (int k = 1; k <=50; k++)    {        set<int>st;        for (int i = 0; i < n; i++)        {            if(present[k]  && !vis[i][k]){               for (int j=1; j<=50; j++)               {                  if(vis[i][j])st.insert(j);               }            }        }        mx_cnt=max(mx_cnt,(int)st.size());    }    cout<<mx_cnt<<'\n';    } int main(){int t;cin>>t;while (t--){    solve();}     return 0;}