#include<bits/stdc++.h>using namespace std;typedef long long ll; void solve(){    ll n;    cin>>n;    vector<ll>arr(n);    ll mn_idx=0;    for (ll i = 0; i < n; i++)    {        cin>>arr[i];        if(arr[i]<arr[mn_idx])            mn_idx=i;    }        for (ll i = mn_idx; i < n-1; i++)    {        if(arr[i]>arr[i+1]){            cout<<-1<<'\n';            return;        }    }    cout<<mn_idx<<'\n'; } int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}