#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}ll gcd_of_differences(vector<ll> &a) {    ll g = 0;    for (int i = 1; i < a.size(); i++) {        g = __gcd(g, a[i] - a[i - 1]);    }    return g;}void solve(){    ll n;    cin>>n;    vector<ll>arr(n);    f(n,arr);    if(n==1){        cout<<1<<'\n';        return;    }    sort(begin(arr),end(arr));    ll diff=gcd_of_differences(arr);        ll k=1;        ll x=arr[n-1];        for (ll i = n-2; i>=0; i--)        {            if(arr[n-1]-arr[i]==k*diff)k++;            else {                x=arr[n-1]-k*diff;                break;            }        }        ll ans2=0;        if(x==arr[n-1]){            x=arr.back()+diff;            for(ll i:arr)ans2+=(x-i)/diff;            cout<<ans2<<'\n';        }        else{            for(ll i:arr)ans2+=(arr[n-1]-i)/diff;            cout<<ans2+(arr[n-1]-x)/diff<<'\n';                }    } int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}