#include<bits/stdc++.h>using namespace std;void f(int n,vector<int>&a){    for(int i=0;i<n;i++)cin>>a[i];} int mask=INT_MAX;void solve(){    int n;    cin>>n;    vector<int>arr(n);    f(n,arr);    multiset<int>st;    int ans=0;    for(int i:arr){        if(st.find(mask^i)!=st.end()){            ans++;            st.erase(st.find(mask^i));        }        else st.insert(i);    }cout<<ans+st.size()<<'\n';} int main(){ios::sync_with_stdio(0);cin.tie(0);int t;cin>>t;while (t--){    solve();}     return 0;}