#include<bits/stdc++.h>using namespace std;typedef long long ll; void solve(){    ll n,x,y;    cin>>n>>x>>y;    vector<ll>arr(n),rem_x(n),rem_y(n);    map<pair<ll,ll>,ll>mp;    for (ll i = 0; i < n; i++)    {        cin>>arr[i];        rem_x[i]=arr[i]%x;        rem_y[i]=arr[i]%y;        mp[{rem_x[i],rem_y[i]}]++;    }    ll ans=0;    for (ll i = 0; i < n; i++)    {       ll k=(x-rem_x[i])%x;       if(k==rem_x[i]){       if(mp[{k,rem_y[i]}]>1){        ans+=mp[{k,rem_y[i]}]-1;        mp[{rem_x[i],rem_y[i]}]--;       }       }       else{        if(mp[{k,rem_y[i]}]){            ans+=mp[{k,rem_y[i]}];            mp[{rem_x[i],rem_y[i]}]--;        }       }     }    cout<<ans<<'\n';    } int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}     return 0;}