#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=n-1;i>=0;i--)cin>>a[i];}void solve(){    ll n,m;    cin>>n>>m;    vector<ll>a(n),b(n),dp(n+1),pref(n+1);    f(n,a),f(n,b);    pref[0]=0,pref[1]=b[0];    for (ll i=2; i<=n; i++) pref[i]=b[i-1]+pref[i-1];            //  dp[i] = minimum cost to reach index i from left    dp[0]=0,dp[1]=a[0];    ll best=min(0ll,dp[1]-pref[1]);    for (ll i=2; i<=n; i++)    {        dp[i]=a[i-1]+pref[i-1]+best;        best=min(best,dp[i]-pref[i]);    }    ll ans=LLONG_MAX;    for (ll i=n; i>n-m; i--)    {        ans=min(ans,dp[i]);    }    cout<<ans<<'\n';} int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}     return 0;}