#include <bits/stdc++.h>using namespace std;using ll = long long; void solve() {    ll n, k;    cin >> n >> k;    vector<ll> a(n);    for (ll &x : a) cin >> x;     ll l = 0, r = n - 1;    ll ans = 0;    int f = 1;      while (k > 0 && l <= r) {        if (l == r) {            if (a[l] <= k) ans++;            break;        }         ll x = a[l];        ll y = a[r];        ll z = min(x, y);         if (f == 1) {            if (z == x) {                if (k < 2*z - 1) break;                k -= 2*z - 1;                a[r] -= (z - 1);                l++;                f = 0;                ans++;            } else {                if (k < 2*z) break;                k -= 2*z;                a[l] -= z;                r--;                ans++;            }        } else {            if (z == y) {                if (k < 2*z - 1) break;                k -= 2*z - 1;                a[l] -= (z - 1);                r--;                f = 1;                ans++;            } else {                if (k < 2*z) break;                k -= 2*z;                a[r] -= z;                l++;                ans++;            }        }    }     cout << ans << "\n";} int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);     int t;    cin >> t;    while (t--) solve();}