#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}ll find(vector<ll>&p,vector<ll>&a,ll pos,ll k){    ll n=p.size();    vector<bool>vis(n,0);    ll best_score=0,presum=0;    while (!vis[pos] && k>0)    {       vis[pos]=1;       best_score=max(best_score,presum+1ll*k*a[pos]);       presum+=a[pos];       pos=p[pos]-1;       k--;    }    return best_score;   }void solve(){    ll n,k,xb,xs;    cin>>n>>k>>xb>>xs;    vector<ll>p(n),a(n);    f(n,p),f(n,a);     ll bs=find(p,a,xb-1,k);    ll ss=find(p,a,xs-1,k);    cout<<(bs>ss?"Bodya\n":bs<ss?"Sasha\n":"Draw\n");} int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}     return 0;}