#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n,k;    cin>>n>>k;    ll max_limit,terms;    if(n&1){        terms=(n-1)/2;        max_limit=2*terms*(1+terms);    }    else{        terms=n/2;        max_limit=2*terms*terms;    }    if(k>max_limit || k&1){        cout<<"No"<<'\n';        return;    }    vector<ll>ans(n+1);    ll rem=k/2;    for (ll i =1;i<=n; i++)ans[i]=i;            for (ll i = 1; i <=n && rem>0; i++)        {            ans[i]=min(rem+i,n-i+1);            ans[ans[i]]=i;            rem-=(ans[i]-i);        }    cout<<"Yes"<<'\n';    for (ll i = 1; i <=n; i++)cout<<ans[i]<<" ";        cout<<'\n';}int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}Â     return 0;}