#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}ll n;void solve(){    cin>>n;    vector<ll>arr(n),ps(n),ss(n);    f(n,arr);    ps[0]=arr[0];    ss[n-1]=arr[n-1];    for (ll i = 1; i < n; i++)ps[i]=ps[i-1]+arr[i];    for (ll i = n-2; i>=0; i--)ss[i]=ss[i+1]+arr[i];     ll ans=abs(ps[n-1]);    for (ll i = 0; i < n-1; i++)    {        ans=max(ans,abs(ps[i])+ss[i+1]);    }    cout<<ans<<'\n';        } int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}     return 0;}