#include<bits/stdc++.h>using namespace std;typedef long long ll;void solve(){    ll n;    cin>>n;    if(n==1){        cout<<1<<'\n';        cout<<1<<'\n';        return;    }    vector<ll>ans;    vector<ll>vis(60,0);    ll cnt=0;    for (ll i = 0; i < 60; i++)    {        ll val=(1ll<<i);        if((val&n)!=0ll){            vis[i]=1;            cnt++;        }    }    if(cnt==1){        cout<<1<<'\n';        cout<<n<<'\n';        return;    }    ans.push_back(n);    for (ll i = 0; i < 60; i++)    {        if(vis[i])ans.push_back(n-(1ll<<i));    }    cout<<ans.size()<<'\n';    for (ll i = ans.size()-1; i>=0; i--)    {       cout<<ans[i]<<" ";    }    cout<<'\n';} int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}      return 0;}