#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n,k;    cin>>n>>k;    vector<ll>l(n),r(n);    f(n,l),f(n,r);    ll ans=0;    vector<ll>minis(n);    for (int i = 0; i < n; i++){        ans+=(max(l[i],r[i]));        minis[i]=min(l[i],r[i]);    }    sort(rbegin(minis),rend(minis));    for (int i = 0; i < n && k>1; i++)    {        ans+=minis[i];        k--;    }    cout<<ans+1<<'\n';       } int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}     return 0;}