#include<bits/stdc++.h>using namespace std;void f(int n,vector<int>&a){    for(int i=0;i<n;i++)cin>>a[i];} void solve(){    int w,h,a,b,x1,y1,x2,y2;    cin>>w>>h>>a>>b>>x1>>y1>>x2>>y2;     if(x1==x2){        if(abs(y1-y2)%b!=0)cout<<"No";        else cout<<"Yes";        cout<<'\n';    }    else if (y1==y2)    {        if(abs(x1-x2)%a==0)cout<<"Yes";        else cout<<"No";        cout<<'\n';    }    else{        if((x1-x2)%a==0 || (y1-y2)%b==0)cout<<"Yes";        else cout<<"No";        cout<<'\n';    }} int main(){int t;cin>>t;while (t--){    solve();}     return 0;}