#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];} void solve(){    ll n;    cin>>n;    vector<ll>arr(n);    f(n,arr);        ll y = (2*arr[0]-arr[1])/(n+1);    ll x = y-arr[0]+arr[1];     if(x<0 || y<0){        cout<<"NO"<<'\n';        return;    }     for (ll i = 0; i < n; i++)    {        arr[i]-=((i+1)*x+(n-i)*y);        if(arr[i]!=0){            cout<<"NO"<<'\n';            return;        }    }    cout<<"YES"<<'\n';    return;} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}