#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}bool cmp(const vector<ll>&a1,const vector<ll>&a2){    if(a1[0]!=a2[0])return a1[0]<a2[0];    if(a1[1]!=a2[1])return a1[1]<a2[1];     return a1[2]>a2[2];}void solve(){    ll n,k;    cin>>n>>k;    vector<vector<ll>>arr(n,vector<ll>(3));    for (ll i = 0; i < n; i++)        cin>>arr[i][0]>>arr[i][1]>>arr[i][2];        sort(begin(arr),end(arr),cmp);    for (ll i = 0; i < n; i++)    {        if(k>=arr[i][0] && k<=arr[i][1] && k<arr[i][2])k=arr[i][2];    }    cout<<k<<'\n';    } int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}