#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];} void solve(){    ll n,m;    cin>>n>>m;    vector<ll>arr(m);    f(m,arr);     // ll max_num=arr[m-1],max_freq=max(0ll,n+1-arr[m-1]);    // for (ll i = n-2; i>=0; i--)    // {           // }    bool f=true;    for (ll i = 1; i<m; i++)    {        if(arr[i]<=arr[i-1]){            f=false;            break;        }    }    if(f){        cout<<n-arr[m-1]+1<<'\n';        return;    }    else cout<<1<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}