#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n,k;    cin>>n>>k;    string s;    cin>>s;    ll prev=-1,cnt=0;    for (ll i = 0; i < n; i++)    {        if(s[i]=='1'){            if(prev==-1)                cnt++;            else{                if(prev+k-1<i)cnt++;            }            prev=i;        }    }    cout<<cnt<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}