#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll m;    cin>>m;    vector<ll>arr(m);    f(m,arr);    ll mx=0;      for (ll i = 0; i < m; i++)    {        mx=max(arr[i],mx);        if(i&1)arr[i]=max(arr[i],mx);        }    ll cnt=0;     for (ll i = 0; i < m; i++)    {        if(i%2==0){            if(i==0 && arr[i]>=arr[i+1])cnt+=(1+arr[i]-arr[i+1]);            if(i==m-1 && arr[i]>=arr[i-1])cnt+=(1+arr[i]-arr[i-1]);            if(i!=0 && i!=m-1 && (arr[i]>=arr[i+1] || arr[i]>=arr[i-1]))cnt+=(1+arr[i]-min(arr[i+1],arr[i-1]));            }    }    cout<<cnt<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}