#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    set<ll>st;    for (ll i = 0; i < n; i++)    {       ll x;       cin>>x;       st.insert(x);    }    if(st.find(0)==st.end())cout<<0<<'\n';    else{        ll i=0;        while (st.size()>0)        {            auto it=st.begin();            if(*it==i)i++;            else {                cout<<i<<'\n';                return;            }            st.erase(it);        }        cout<<i<<'\n';    }} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}