#include<bits/stdc++.h>using namespace std;typedef long long ll; void solve(){    ll n;    cin>>n;    vector<ll>arr(n);    for(ll i=0;i<n;i++){        cin>>arr[i];    }     sort(begin(arr),end(arr));    for(ll i=0;i<n;i++){        if(i!=0)arr[i]+=arr[i-1];    }     ll first=-1,last=-1;    for (ll i = n-1; i>=0; i--)    {       if(arr[i]!=0){        last=i;        break;       }    }    for (ll i = 0; i < n; i++)    {        if(arr[i]!=0){            first=i;            break;        }    }     ll increment=arr[last],gap=last-first+1;    if(n==increment)cout<<1<<'\n';    else{        if(increment-gap>=n-1)cout<<gap<<'\n';        else{            cout<<increment-n+1<<'\n';        }    }} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}