#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}bool check(ll c,vector<ll>&q,vector<ll>&r,ll k){    for (ll i = 0; i < c; i++)    {        ll val=(q[i]+1)*(r[c-1-i]+1)-1;        if(val>k)return false;    }    return true;}void solve(){    ll n,k;    cin>>n>>k;    vector<ll>q(n),r(n);    f(n,q),f(n,r);    sort(q.begin(),q.end());    sort(begin(r),end(r));         ll low=0,high=n;    while (low<=high)    {        ll mid=low+(high-low)/2;        if(check(mid,q,r,k))low=mid+1;        else high=mid-1;    }    cout<<high<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}